INSERT INTO `nb_note` VALUES ('5', 'package me.wuwenbin.noteblogv5.controller.frontend;\n\nimport cn.hutool.core.util.StrUtil;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.core.metadata.OrderItem;\nimport com.baomidou.mybatisplus.core.toolkit.Wrappers;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport me.wuwenbin.noteblogv5.controller.common.BaseController;\nimport me.wuwenbin.noteblogv5.model.entity.Note;\nimport me.wuwenbin.noteblogv5.service.interfaces.content.NoteService;\nimport org.springframework.stereotype.Controller;\nimport org.springframework.ui.Model;\nimport org.springframework.web.bind.annotation.GetMapping;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.RequestParam;\n\n/**\n * @author wuwen\n */\n@Controller\n@RequestMapping(&quot;/note&quot;)\npublic class NoteController extends BaseController {\n\n    private final NoteService noteService;\n\n    public NoteController(NoteService noteService) {\n        this.noteService = noteService;\n    }\n\n    @GetMapping()\n    public String notePage(Model model, Page&lt;Note&gt; page,\n                           @RequestParam(required = false, defaultValue = &quot;1&quot;) int p, String w) {\n        page.setCurrent(p &lt;= 0 ? 1 : p);\n        page.addOrder(OrderItem.desc(&quot;post&quot;));\n        IPage&lt;Note&gt; notePage = noteService.page(page,\n                Wrappers.&lt;Note&gt;query().like(StrUtil.isNotEmpty(w), &quot;clear_content&quot;, w));\n        model.addAttribute(&quot;notePage&quot;, notePage);\n        return &quot;frontend/note&quot;;\n    }\n\n}\n\n', '<pre><code class=\"lang-java\">package me.wuwenbin.noteblogv5.controller.frontend;\n\nimport cn.hutool.core.util.StrUtil;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.core.metadata.OrderItem;\nimport com.baomidou.mybatisplus.core.toolkit.Wrappers;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport me.wuwenbin.noteblogv5.controller.common.BaseController;\nimport me.wuwenbin.noteblogv5.model.entity.Note;\nimport me.wuwenbin.noteblogv5.service.interfaces.content.NoteService;\nimport org.springframework.stereotype.Controller;\nimport org.springframework.ui.Model;\nimport org.springframework.web.bind.annotation.GetMapping;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.RequestParam;\n\n/**\n * @author wuwen\n */\n@Controller\n@RequestMapping(&quot;/note&quot;)\npublic class NoteController extends BaseController {\n\n    private final NoteService noteService;\n\n    public NoteController(NoteService noteService) {\n        this.noteService = noteService;\n    }\n\n    @GetMapping()\n    public String notePage(Model model, Page&lt;Note&gt; page,\n                           @RequestParam(required = false, defaultValue = &quot;1&quot;) int p, String w) {\n        page.setCurrent(p &lt;= 0 ? 1 : p);\n        page.addOrder(OrderItem.desc(&quot;post&quot;));\n        IPage&lt;Note&gt; notePage = noteService.page(page,\n                Wrappers.&lt;Note&gt;query().like(StrUtil.isNotEmpty(w), &quot;clear_content&quot;, w));\n        model.addAttribute(&quot;notePage&quot;, notePage);\n        return &quot;frontend/note&quot;;\n    }\n\n}\n</code></pre>\n', '```java\npackage me.wuwenbin.noteblogv5.controller.frontend;\n\nimport cn.hutool.core.util.StrUtil;\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport com.baomidou.mybatisplus.core.metadata.OrderItem;\nimport com.baomidou.mybatisplus.core.toolkit.Wrappers;\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport me.wuwenbin.noteblogv5.controller.common.BaseController;\nimport me.wuwenbin.noteblogv5.model.entity.Note;\nimport me.wuwenbin.noteblogv5.service.interfaces.content.NoteService;\nimport org.springframework.stereotype.Controller;\nimport org.springframework.ui.Model;\nimport org.springframework.web.bind.annotation.GetMapping;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.RequestParam;\n\n/**\n * @author wuwen\n */\n@Controller\n@RequestMapping(\"/note\")\npublic class NoteController extends BaseController {\n\n    private final NoteService noteService;\n\n    public NoteController(NoteService noteService) {\n        this.noteService = noteService;\n    }\n\n    @GetMapping()\n    public String notePage(Model model, Page<Note> page,\n                           @RequestParam(required = false, defaultValue = \"1\") int p, String w) {\n        page.setCurrent(p <= 0 ? 1 : p);\n        page.addOrder(OrderItem.desc(\"post\"));\n        IPage<Note> notePage = noteService.page(page,\n                Wrappers.<Note>query().like(StrUtil.isNotEmpty(w), \"clear_content\", w));\n        model.addAttribute(\"notePage\", notePage);\n        return \"frontend/note\";\n    }\n\n}\n\n```', '2019-09-19 23:29:54.315000', '2019-09-04 23:24:58.000000', '1', 'A1BAAAAAedfafsafasfsafasf21', '0');
INSERT INTO `nb_note` VALUES ('6', '网页收藏在手机上的浏览器里看到有价值的网页时，点击底端的 分享按钮，选择 为知笔记，网页内容就保存到为知笔记啦！\n', '<h4 id=\"h4-u7F51u9875u6536u85CF\"><a name=\"网页收藏\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>网页收藏</h4><p>在手机上的浏览器里看到有价值的网页时，点击底端的 <strong>分享按钮</strong>，选择 <strong>为知笔记</strong>，网页内容就保存到为知笔记啦！</p>\n', '#### 网页收藏\n\n在手机上的浏览器里看到有价值的网页时，点击底端的 **分享按钮**，选择 **为知笔记**，网页内容就保存到为知笔记啦！', '2019-09-19 23:47:51.402000', '2019-09-19 23:46:38.560000', '1', 'dsadsa1', '0');
INSERT INTO `nb_note` VALUES ('7', '简单两步完成操作\n点击笔记列表底端的羽毛笔，在模板中心选择 Markdown在正文中撰写 Markdown 语法，点击完成，就可以看到渲染后美美的笔记啦。\n简单介绍几种常用语法\n标题在行首插入 1 到 6个#，分别表示标题 1 到标题 6\n', '<h4 id=\"h4-u7B80u5355u4E24u6B65u5B8Cu6210u64CDu4F5C\"><a name=\"简单两步完成操作\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>简单两步完成操作</h4><ol>\n<li><strong>点击</strong>笔记列表底端的羽毛笔，在模板中心选择 <strong>Markdown</strong></li><li>在正文中撰写 Markdown 语法，点击完成，就可以看到渲染后美美的笔记啦。</li></ol>\n<p>简单介绍几种常用语法</p>\n<h5 id=\"h5-u6807u9898\"><a name=\"标题\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>标题</h5><p>在行首插入 1 到 6个#，分别表示标题 1 到标题 6</p>\n', '#### 简单两步完成操作\n1. **点击**笔记列表底端的羽毛笔，在模板中心选择 **Markdown**\n2. 在正文中撰写 Markdown 语法，点击完成，就可以看到渲染后美美的笔记啦。\n\n简单介绍几种常用语法\n\n##### 标题\n在行首插入 1 到 6个#，分别表示标题 1 到标题 6', null, '2019-09-19 23:46:49.349000', '1', 'rrewrewrewrewr1', '0');
INSERT INTO `nb_note` VALUES ('8', ' ##### 这是标题5\n ###### 这是标题6\n这是标题5这是标题6列表有序列表有序列表则使用数字加一个英文句点，并加空格，再输入文字\n1. 你好\n1. 为知笔记\n1. 加油！\n\n你好为知笔记加油！~~~~\n', '<pre><code> ##### 这是标题5\n ###### 这是标题6\n</code></pre><h5 id=\"h5--5\"><a name=\"这是标题5\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>这是标题5</h5><h6 id=\"h6--6\"><a name=\"这是标题6\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>这是标题6</h6><h5 id=\"h5-u5217u8868\"><a name=\"列表\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>列表</h5><h6 id=\"h6-u6709u5E8Fu5217u8868\"><a name=\"有序列表\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>有序列表</h6><p>有序列表则使用数字加一个英文句点，并加空格，再输入文字</p>\n<pre><code>1. 你好\n1. 为知笔记\n1. 加油！\n</code></pre><ol>\n<li>你好</li><li>为知笔记</li><li>加油！~~~~</li></ol>\n', '```\n ##### 这是标题5\n ###### 这是标题6\n```\n\n##### 这是标题5\n###### 这是标题6\n\n\n\n##### 列表\n###### 有序列表\n有序列表则使用数字加一个英文句点，并加空格，再输入文字\n\n```\n1. 你好\n1. 为知笔记\n1. 加油！\n```\n\n1. 你好\n1. 为知笔记\n1. 加油！~~~~', null, '2019-09-19 23:46:59.794000', '1', 'dasdaqweqdw1', '0');
INSERT INTO `nb_note` VALUES ('9', '无序序列表无序列表使用星号、加号或是减号作为列表标记，并加一个空格，再输入文字\n+ Android\n+ iPhone\n+ iPad\n\nAndroidiPhoneiPad\n链接[描述]（链接地址）\n', '<h6 id=\"h6-u65E0u5E8Fu5E8Fu5217u8868\"><a name=\"无序序列表\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>无序序列表</h6><p>无序列表使用星号、加号或是减号作为列表标记，并加一个空格，再输入文字</p>\n<pre><code>+ Android\n+ iPhone\n+ iPad\n</code></pre><ul>\n<li>Android</li><li>iPhone</li><li>iPad</li></ul>\n<h5 id=\"h5-u94FEu63A5\"><a name=\"链接\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>链接</h5><p>[描述]（链接地址）</p>\n', '###### 无序序列表\n\n无序列表使用星号、加号或是减号作为列表标记，并加一个空格，再输入文字\n\n```\n+ Android\n+ iPhone\n+ iPad\n```\n\n+ Android\n+ iPhone\n+ iPad\n\n##### 链接\n[描述]（链接地址）\n', null, '2019-09-19 23:47:06.969000', '1', 'dadsadsadada1', '0');
